	#{roleId,jdbcType=BIGINT}

mapper.select:
select

from sys_role 


		and `role_name` like concat('%',#{roleName,jdbcType=VARCHAR},'%') 


		and create_user_id = #{createUserId,jdbcType=BIGINT} 




		order by ${sidx} ${order}


		order by role_id asc



	limit #{offset}, #{limit}

select count(*) from sys_role 


		and `role_name` like concat('%',#{roleName,jdbcType=VARCHAR},'%') 


		and create_user_id = #{createUserId,jdbcType=BIGINT}
